  "content": "$(document).ready(function() {
 $('#new-todo').keydown(function(event) {
  if (event.keyCode === 13) {
   const todoText = $(this).val();
   $(this).val('');

   const listItem = $('<li>').text(todoText).addClass('collection-item');
   const deleteBtn = $("<a href='javascript: void(0);' class='secondary-content single-line'><i class='material-icons'>delete</i></a>");

   deleteBtn.click(function() {
    $(this).parent().remove();
   });

   listItem.append(deleteBtn);
   $('#todo-list').append(listItem);
  }
 });
});"
}

