  actionType: "editFile",
  path: "index.html",
  content: `<!DOCTYPE html>
<html>
  <head>
    <meta charset="UTF-8" />
    <title>Todo App</title>
    <link
      href="https://fonts.googleapis.com/icon?family=Material+Icons"
      rel="stylesheet"
    />
    <link
      rel="stylesheet"
      href="https://cdnjs.cloudflare.com/ajax/libs/materialize/1.0.0/css/materialize.min.css"
    />
    <link rel="stylesheet" href="style.css" />
  </head>
  <body>
    <div class="container">
      <h3>Todo App</h3>
      <div class="input-field">
        <input type="text" id="todo-input" />
        <label for="todo-input">Add a Todo</label>
      </div>
      <ul class="collection" id="todo-items">
        <!-- Todo Items Will Go Here -->
      </ul>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.3.1/jquery.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/materialize/1.0.0/js/materialize.min.js"></script>
    <script src="app.js"></script>
  </body>
</html>`
}

Action 7:
{
  actionType: "editFile",
  path: "app.js",
  content: `$(document).ready(function() {

  // UI Elements
  const todoInput = $('#todo-input');
  const todoItems = $('#todo-items');

  // Add Todo Functionality
  todoInput.keypress(function(event) {
    if (event.which === 13) {
      const todo = todoInput.val();
      if (todo) {
        const todoItem = $('<li class="collection-item">')
          .text(todo)
          .append($('<a href="#" class="secondary-content remove-item"><i class="material-icons">delete</i></a>'));

        todoItems.append(todoItem);

        todoInput.val(''); // clear input
      }
    }
  });

  // Remove Todo Functionality
  todoItems.on('click', '.remove-item', function(event) {
    $(this)
      .parents('.collection-item')
      .remove();
  });
});`
} 

